import React, { Component }  from 'react';
import DishDetail from './DishdetailComponent'
import { Card, CardImg, CardImgOverlay, CardText, CardBody, CardTitle } from 'reactstrap';
class Menu extends Component {

    constructor(props){
        super(props);

        this.state= {
            selectedDish: null,  
            dishData: this.props.dishes,    
            
        }
    }

    onDishSelect(dish) {
        this.setState({
            selectedDish: dish
        });
    }

    
    

    render() {
        
        const menu = this.props.dishes.map( (dish) => {
             return (
                <div key={dish.id} className="col-12 col-md-5 m-1">
                    <Card onClick={ () => this.onDishSelect(dish) }>

                    <CardImg  width="100%" src={dish.name} alt={dish.name} />
                   

                    <CardImgOverlay>
                            <CardTitle>{dish.name}</CardTitle>
                    </CardImgOverlay>

                    </Card>
                </div>
             );
             });
            
        return ( 
            <div className="container">
                <div className="row">
                    
                        {menu}
                       
                  
                </div>

                <div className="row">
                    <DishDetail  dishData={this.state.dishData} dish={this.state.selectedDish}/>
                    
                </div>
            </div>

        );
    }
}

export default Menu;
