import Menu from './MenuComponent'
import { Card, CardImg, CardImgOverlay, CardText, CardBody, CardTitle } from 'reactstrap';

class DishDetail extends Component {

    constructor(props){
        super(props);

        this.state= {   
        
         }
       
    }
   
   
    renderDish(dish) {  
        dish =this.props.dish ;
        console.log(dish)
        if (dish != null)
            return(
                <div className="row">
                    <div className="col-12 col-md-5 m-1">
                <Card>
                    <CardImg width="550%" src={dish.image} alt={dish.name} />
                    <CardBody>
                      <CardTitle>{dish.name}</CardTitle>
                      <CardText>{dish.description}</CardText>
                    </CardBody>
                </Card>
                </div>
                </div>
            );
        else
            return(
                <div></div>
            );
    }
   

    render(){ 
        
       return (
       // console.log(this.props.dishData)
        //console.log(this.props.selecteDish)
        <div className="row">
            <div  className="col-12 col-md-5 m-1">
                {this.renderDish()}
            </div>
        </div>    
        
       )}
}

export default DishDetail
