import React, { Component }  from 'react';
import Menu from './MenuComponent'
import { Card, CardImg, CardImgOverlay, CardText, CardBody, CardTitle } from 'reactstrap';

class DishDetail extends Component {

    constructor(props){
        super(props);
 
        this.state= {
               
        }
    }

   

    render(){
       // console.log(this.props.dishData)
        //console.log(this.props.selecteDish)
        
        const DishDetail =this.props.selecteDish.map( (dish) =>  {
               if( DishDetail != null) {
                return(
                    <div>
                    <Card>
                        
                        <CardImg  width="100%" src={dish.image} alt={dish.selecteDish.name} />
                        <CardBody>
                            <CardTitle>{dish.selecteDish.name}</CardTitle>
                            <CardText>{dish.description}</CardText>
                        </CardBody>
                    </Card>
                    </div>
                )
            }else {
                return(
                    <div></div>
                )    
            } //else end
        })   // const DishDetail end

        return ( 
            <div className="container">
                <div className="row">                   
                        {DishDetail} 
                </div>              
            </div>
        );
    }
}

export default DishDetail
